import React from 'react';

class ModalDialog extends React.Component {

  render() {
    return (
      <div>
        <button type="button" class="btn btn-success btn-md" id="myBtn" data-toggle="modal" data-target="#myModal" data-show="true">Modal (data-show="true")</button>
        {/* <button type="button" class="btn btn-danger btn-md" id="myBtn2" data-toggle="modal" data-target="#myModal2" data-show="false">Modal (data-show="false")</button> */}
        <div class="modal fade" id="myModal" role="dialog">
          <div class="modal-dialog" style={{maxWidth:1300}}>
            <div class="modal-content">
              <div class="modal-header">
                <h4 class="modal-title">Modal Options</h4>
                <button type="button" class="close" data-dismiss="modal">&times;</button>
              </div>
              <div class="modal-body">
                <p>Modal content..</p>
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
              </div>
            </div>

          </div>
        </div>
      </div>
    )
  }
}

// React.render(<ModalDialog />, document.getElementById("ModalDialog"));

// React.render(<Button />, document.getElementById('modal'));

export default ModalDialog;
