import React from 'react';

class Update extends React.Component{
    constructor(props){
        super(props);
        this.componentDidMount = this.componentDidMount.bind(this);
        this.uid = React.createRef();
        this.fname = React.createRef();
        this.lname = React.createRef();
        this.email = React.createRef();
        this.phone = React.createRef();
        this.order = React.createRef();
        this.orderno = React.createRef();
        this.items = React.createRef();
        this.price = React.createRef();
        this.total = React.createRef();
        this.state = {
           user: {
            uid: '',
            fname: '',
            lname: '',
            email: '',
            phone: '',
            order: '',
            orderno: '',
            items: '',
            price: '',
            total: ''
           }
        }
        console.log("Constructor", this.props);

        // this.updateData = this.updateData.bind(this);
    }
    componentWillReceiveProps(nextProps) {
        if(this.props.orderno !== nextProps.user.orderno) {
          this.setState({user: {orderno: nextProps.user.orderno}})
        }
     }
    update = (name, e) => {
        e.preventDefault();
        this.setState({ 
            user: {
                [name]: e.target.type === 'checkbox' ? e.target.checked : e.target.value
            }
        });
    }
    componentDidMount() {
        // alert("componentDidMount");
        alert(this.props.keys);
        console.log(this.props);
        /* this.setState({
            user: ""
        }); */
    }

    updateData = (user) => {
        console.log(user);
        // this.refs.orderno.value = user.orderno;
        // this.setState({user: {orderno: "1111"}});
      }





    render() {
        // console.log("render", this.props.user);
        // this.updateData(this.props.user);
                // console.log(this.props.user);
                // this.updateData(this.props.user);
        return (
            <div className='row'>
            <div className='col-xl-12'>
            <h4>Update Order</h4>
            <form>
                <div className="form-row">
                <input type='hidden' ref={this.uid} />
                <div className="form-group col-md-6"> 
                    <label>Oder No</label>
                    <input type="text" ref={this.orderno} value={this.state.user.orderno || ''}  className="form-control" onChange={(e) => this.update("orderno", e)} placeholder="Oder No" />
                    <h4>{this.state.user.orderno}</h4>
                </div>
                <div className="form-group col-md-6">
                    <label>Oder Name</label>
                    <input type="text" ref={this.order} value={this.state.user.order || ''} className="form-control" placeholder="Oder Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Number of Items</label>
                    <input type="number" ref={this.item} value={this.state.user.item || ''} className="form-control" placeholder="Number of Items" />
                </div>
                <div className="form-group col-md-6">
                    <label>Item Price</label>
                    <input type="number" ref={this.price} value={this.state.user.price || ''} className="form-control" placeholder="Item Cost" />
                </div>
                <div className="form-group col-md-6">
                    <label>Total</label>
                    <input type="text" ref={this.total} value={this.state.user.total || ''} className="form-control" placeholder="Total Cost" />
                </div>
                <div className="form-group col-md-6">
                    <label>First Name</label>
                    <input type="text" ref={this.fname} value={this.state.user.fname || ''} className="form-control" placeholder="First Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Last Name</label>
                    <input type="text" ref={this.lname} value={this.state.user.email || ''} className="form-control" placeholder="Last Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Email</label>
                    <input type="text" ref={this.email} value={this.state.user.email || ''} className="form-control" placeholder="Email" />
                </div>
                <div className="form-group col-md-6">
                    <label>Mobile / Phone No</label>
                    <input type="number" ref={this.phone} value={this.state.user.phone || ''} className="form-control" placeholder="Mobile / Phone No" />
                </div>
                </div>
                <button type="button" className="btn btn-primary" onClick={ this.addUser }>Save</button>
            </form>
            {/* <div><pre>{JSON.stringify(this.state, null, 2) }</pre></div> */}
            <div>Updates<pre>{JSON.stringify(this.props.user, null, 2) }</pre></div>
            </div>
        </div>
        )
    }

}

export default Update;
