import React from 'react';

class Update extends React.Component{
    constructor(props){
        super(props);
        this.fname = React.createRef();
        this.lname = React.createRef();
        this.email = React.createRef();
        this.phone = React.createRef();
        this.order = React.createRef();
        this.orderno = React.createRef();
        this.items = React.createRef();
        this.price = React.createRef();
        this.total = React.createRef();
        this.updateData = this.updateData.bind(this);
    }

    /* componentDidMount() {
        this.setState({
            user: {name: "Ravi"}
        });
    } */
    // setState({"user": {"name" : "Ravi"}});
    //this is called to before render method
    /* componentWillReceiveProps(){ 
        alert("Alert");
        this.setState({
            user:this.props.user
        })
    }  */  
    componentDidMount(){
        console.log("componentDidMount", this.props.user);
    }
    updateData = () => {
        const user = {
          "email": "email@sample.com",
          "fname": "Ravi",
          "items": "200",
          "lname": "Test",
          "name": "Ravi Test",
          "order": "Ravi Books",
          "orderno": "8001",
          "phone": "8123456789",
          "price": "50",
          "total": "10,000",
          "uid": "1675303448945"
        }
        // this.orderno.value = this.props.user.orderno;
        // this.refs.uid.value = "Ravi";
        /* this.refs.fname.value = this.props.user.fname;
        this.refs.lname.value = this.props.user.lname;
        this.refs.email.value = this.props.user.email;
        this.refs.phone.value = this.props.user.phone;
        this.refs.order.value = this.props.user.order;
        this.refs.orderno.value = this.props.user.orderno;
        this.refs.items.value = this.props.user.items;
        this.refs.price.value = this.props.user.price;
        this.refs.total.value = this.props.user.total; */
      }

    render() {
                console.log(this.props.user);
                this.updateData(this.props.user);
        return (
            <div className='row'>
            <div className='col-xl-12'>
            <h4>Update Order</h4>
            <form>
                <div className="form-row">
                <input type='hidden' ref={this.uid} />
                <div className="form-group col-md-6"> 
                    <label>Oder No</label>
                    <input type="text" ref={this.orderno}  className="form-control" placeholder="Oder No" />
                    <h4>{this.orderno.value}</h4>
                </div>
                <div className="form-group col-md-6">
                    <label>Oder Name</label>
                    <input type="text" ref={this.order} className="form-control" placeholder="Oder Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Number of Items</label>
                    <input type="number" ref={this.item} className="form-control" placeholder="Number of Items" />
                </div>
                <div className="form-group col-md-6">
                    <label>Item Price</label>
                    <input type="number" ref={this.price} className="form-control" placeholder="Item Cost" />
                </div>
                <div className="form-group col-md-6">
                    <label>Total</label>
                    <input type="text" ref={this.total} className="form-control" placeholder="Total Cost" />
                </div>
                <div className="form-group col-md-6">
                    <label>First Name</label>
                    <input type="text" ref={this.fname} className="form-control" placeholder="First Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Last Name</label>
                    <input type="text" ref={this.lname} className="form-control" placeholder="Last Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Email</label>
                    <input type="text" ref={this.email} className="form-control" placeholder="Email" />
                </div>
                <div className="form-group col-md-6">
                    <label>Mobile / Phone No</label>
                    <input type="number" ref={this.phone} className="form-control" placeholder="Mobile / Phone No" />
                </div>
                </div>
                <button type="button" className="btn btn-primary" onClick={ this.addUser }>Save</button>
            </form>
            {/* <div><pre>{JSON.stringify(this.state, null, 2) }</pre></div> */}
            <div>Updates<pre>{JSON.stringify(this.props.user, null, 2) }</pre></div>
            </div>
        </div>
        )
    }

}

export default Update;
