import React from 'react';
import Update from './update'
class ModalDialog extends React.Component {

  render() {
    const maxwidth = { "max-width": "80%" };
    return (
      <div>
        <button type="button" class="btn btn-success btn-md" id="myBtn" data-toggle="modal" data-target="#myModal" data-show="true">Modal (data-show="true")</button>
        <div><pre>{JSON.stringify(this.props, null, 2) }</pre></div>
        <div class="modal fade" id="myModal" role="dialog">
          <div class="modal-dialog" style={maxwidth}>
            <div class="modal-content">
              <div class="modal-header">
                <h4 class="modal-title">Modal Options</h4>
                <button type="button" class="close" data-dismiss="modal">&times;</button>
              </div>
              <div class="modal-body">
                <Update user={this.props.user} />
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
              </div>
            </div>

          </div>
        </div>
      </div>
    )
  }
}

export default ModalDialog;
