import React from 'react';

class Update extends React.Component{
    constructor(props){
        super(props);
        this.updateUser = this.updateUser.bind(this);
        this.update = this.update.bind(this);
        this.uid = React.createRef();
        this.fname = React.createRef();
        this.lname = React.createRef();
        this.email = React.createRef();
        this.phone = React.createRef();
        this.order = React.createRef();
        this.orderno = React.createRef();
        this.items = React.createRef();
        this.price = React.createRef();
        this.total = React.createRef();
        this.state = {
           user: {
            uid: '',
            fname: '',
            lname: '',
            email: '',
            phone: '',
            order: '',
            orderno: '',
            items: '',
            price: '',
            total: ''
           }
        }
    }
    componentWillReceiveProps(nextProps) {
        if(this.props.user !== nextProps.user) {
          this.setState({user: nextProps.user})
        }
     }
    update = (name, e) => {
        e.preventDefault();
        console.log("Field Update", name, e.target.value);
        this.setState({ 
            user: {
                ...this.state.user,
                [name]: e.target.type === 'checkbox' ? e.target.checked : e.target.value
            }
        });
    }

    updateUser = (event) => {
        event.preventDefault();
        this.props.update(this.state.user);
        console.log("Update form details", this.state.user);
    }

    updateData = (user) => {
        console.log(user);
        // this.refs.orderno.value = user.orderno;
        // this.setState({user: {orderno: "1111"}});
    }
    render() {
        return (
            <div className='row'>
            <div className='col-xl-12'>
            <h4>Update Order</h4>
            <div>Updates<pre>{JSON.stringify(this.state.user, null, 2) }</pre></div>
            <form>
                <div className="form-row">
                <input type='hidden' ref={this.uid} value={this.state.user.uid || ''} />
                <div className="form-group col-md-6"> 
                    <label>Oder No</label>
                    <input type="text" ref={this.orderno} value={this.state.user.orderno || ''}  className="form-control" onChange={(e) => this.update("orderno", e)} placeholder="Oder No" />
                </div>
                <div className="form-group col-md-6">
                    <label>Oder Name</label>
                    <input type="text" ref={this.order} value={this.state.user.order || ''} onChange={(e) => this.update("order", e)} className="form-control" placeholder="Oder Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Number of Items</label>
                    <input type="number" ref={this.items} value={this.state.user.items || ''} onChange={(e) => this.update("items", e)} className="form-control" placeholder="Number of Items" />
                </div>
                <div className="form-group col-md-6">
                    <label>Item Price</label>
                    <input type="number" ref={this.price} value={this.state.user.price || ''} onChange={(e) => this.update("price", e)} className="form-control" placeholder="Item Cost" />
                </div>
                <div className="form-group col-md-6">
                    <label>Total</label>
                    <input type="text" ref={this.total} value={this.state.user.total || ''} onChange={(e) => this.update("total", e)} className="form-control" placeholder="Total Cost" />
                </div>
                <div className="form-group col-md-6">
                    <label>First Name</label>
                    <input type="text" ref={this.fname} value={this.state.user.fname || ''} onChange={(e) => this.update("fname", e)} className="form-control" placeholder="First Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Last Name</label>
                    <input type="text" ref={this.lname} value={this.state.user.lname || ''} onChange={(e) => this.update("lname", e)} className="form-control" placeholder="Last Name" />
                </div>
                <div className="form-group col-md-6">
                    <label>Email</label>
                    <input type="text" ref={this.email} value={this.state.user.email || ''} onChange={(e) => this.update("email", e)} className="form-control" placeholder="Email" />
                </div>
                <div className="form-group col-md-6">
                    <label>Mobile / Phone No</label>
                    <input type="number" ref={this.phone} value={this.state.user.phone || ''} className="form-control" placeholder="Mobile / Phone No" />
                </div>
                </div>
                <button type="button" className="btn btn-primary" onClick={ this.updateUser }>Save</button>
            </form>
            {/* <div><pre>{JSON.stringify(this.state, null, 2) }</pre></div> */}

            </div>
        </div>
        )
    }
}
export default Update;
